import React, { useState, useEffect } from "react";
import "./Slider.css";

const images = [
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSinbTUBiClZfL74ZOFongBCrQV0EpQVxsDIg&s",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "https://i0.wp.com/philippesilberzahn.com/wp-content/uploads/2023/11/chien-et-ia.jpg?resize=896%2C512&ssl=1",
];

const Slider = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="slider-container">
      <div className="carousel">
        {images.map((img, index) => {
          const offset = (index - activeIndex + images.length) % images.length;
          const angle = offset * 90;
          const zIndex = images.length - offset;

          const style = {
            transform: `rotateY(${angle}deg) translateZ(280px)`,
            zIndex,
          };

          return (
            <img
              key={index}
              src={img}
              alt={`Slide ${index + 1}`}
              className={`carousel-image ${offset === 0 ? "active" : ""}`}
              style={style}
              draggable={false}
            />
          );
        })}
      </div>
    </div>
  );
};

export default Slider;
